sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   var requestedfor = g_form.getValue('u_requested_for')
   var getEmailAdr = new GlideAjax("GetEmailAddress");
	getEmailAdr.addParam('sysparm_name','getEmail');
	getEmailAdr.addParam('sysparm_userID',requestedfor);
	getEmailAdr.getXML(populateEmailField);

	function populateEmailField(response){
		var emailAddressFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email',emailAddressFromScriptInclude);
	}
   //Type appropriate comment here, and begin script below

}
